import React, { useState, useRef, useEffect } from 'react';
import { 
  Search, 
  MessageSquare, 
  Home, 
  User, 
  ChevronLeft, 
  PlayCircle, 
  Send, 
  Menu,
  Info,
  Leaf,
  Sun
} from 'lucide-react';

// --- Assets & Theme ---
const THEME = {
  primary: '#4A6741', // Deep Green
  secondary: '#8AA883', // Sage Green
  background: '#F5F7F2', // Light Beige/Cream
  surface: '#FFFFFF',
  textMain: '#2C3E28',
  textSub: '#5C6F58',
};

// --- Mock Data ---
const FILTERS = ["All", "Medicinal", "Culinary", "Beverage", "Ornamental"];

const PLANTS = [
  {
    id: 1,
    name: "Brazilian Spinach",
    scientific: "Alternanthera sissoo",
    image: "https://images.unsplash.com/photo-1628608212574-e8d1976008b8?auto=format&fit=crop&q=80&w=600",
    category: "Culinary",
    overview: "Brazilian Spinach is a low-growing perennial vegetable valued for its crunchy texture and high nutritional value. It is often consumed raw in salads or cooked in soups and stir-fries.",
    characteristics: "Height: 30cm-50cm. Leaves: Crinkled, glossy green. Growth: Spreading ground cover.",
    nutritional: "High in Iron, Vitamin A, and Vitamin C. Contains high levels of carotenoids.",
    uses: "Traditionally used to improve eye health and strengthen the immune system. In modern cuisine, it is a popular spinach substitute.",
    care: "Light: Partial shade to full sun.\nWater: Keep soil moist but not waterlogged.\nSoil: Rich, well-draining soil."
  },
  {
    id: 2,
    name: "Torch Ginger",
    scientific: "Etlingera elatior",
    image: "https://images.unsplash.com/photo-1596726658098-84c207903823?auto=format&fit=crop&q=80&w=600",
    category: "Medicinal",
    overview: "A stunning plant known for its bright pink flower buds which are a key ingredient in local laksa dishes.",
    characteristics: "Height: Up to 5m. Flowers: Large, pink/red waxy petals.",
    nutritional: "Rich in antioxidants and antibacterial properties.",
    uses: "The flower bud is sliced into salads and curries. Traditionally used to treat earaches and clean wounds.",
    care: "Light: Full sun.\nWater: High water requirement.\nSoil: Fertile, high organic matter."
  },
  {
    id: 3,
    name: "Sand Ginger",
    scientific: "Kaempferia galanga",
    image: "https://images.unsplash.com/photo-1615485925763-867862f7253a?auto=format&fit=crop&q=80&w=600",
    category: "Medicinal",
    overview: "A stemless herb arising from a tuberous rhizome, widely used in traditional medicine and cooking.",
    characteristics: "Leaves: Round, lying flat on the ground. Rhizome: Aromatic.",
    nutritional: "Contains essential oils like ethyl cinnamate.",
    uses: "Used to treat indigestion, colds, and pectoral pains. Also used as a spice in rice dishes.",
    care: "Light: Shade.\nWater: Moderate.\nSoil: Loamy soil."
  },
  {
    id: 4,
    name: "Wild Betel Leaf",
    scientific: "Piper sarmentosum",
    image: "https://images.unsplash.com/photo-1621506822262-421733614918?auto=format&fit=crop&q=80&w=600",
    category: "Culinary",
    overview: "Known locally as 'Daun Kaduk', this plant is often confused with the betel nut vine but is smaller and used differently.",
    characteristics: "Leaves: Heart-shaped, glossy. Growth: Creeper.",
    nutritional: "High in calcium and antioxidants.",
    uses: "Leaves are used as wrappers for snacks like 'Miang Kham'. Roots are used to treat toothaches.",
    care: "Light: Partial shade.\nWater: Regular watering.\nSoil: Moist soil."
  },
  {
    id: 5,
    name: "Turmeric",
    scientific: "Curcuma longa",
    image: "https://images.unsplash.com/photo-1615485290382-441e4d049cb5?auto=format&fit=crop&q=80&w=600",
    category: "Medicinal",
    overview: "A flowering plant of the ginger family, the roots of which are used in cooking.",
    characteristics: "Rhizomes: Bright orange-yellow inside.",
    nutritional: "Contains curcumin, a powerful anti-inflammatory compound.",
    uses: "Major ingredient in curry powders. Used in Ayurvedic medicine for inflammation.",
    care: "Light: Full sun to partial shade.\nWater: Keep moist.\nSoil: Well-drained."
  },
  {
    id: 6,
    name: "Butterfly Pea",
    scientific: "Clitoria ternatea",
    image: "https://images.unsplash.com/photo-1631128795552-32d398e09e13?auto=format&fit=crop&q=80&w=600",
    category: "Beverage",
    overview: "A plant species belonging to the family Fabaceae, known for its vivid blue flowers.",
    characteristics: "Flowers: Deep blue with yellow markings. Growth: Vine.",
    nutritional: "Rich in anthocyanins.",
    uses: "Flowers are used to color food blue (like Nasi Kerabu) and to make tea.",
    care: "Light: Full sun.\nWater: Moderate.\nSoil: Neutral pH."
  }
];

const CHAT_MESSAGES = [
  { id: 1, sender: 'bot', text: "Hello! Which herbs are used in traditional healers?" },
  { id: 2, sender: 'bot', text: "There are many herbs used in traditional healing practices. Some of the most notable ones include:\n\n• Bitter Melon (Momordica charantia): Used for blood sugar management.\n• Turmeric (Curcuma longa): Known for anti-inflammatory properties." },
  { id: 3, sender: 'user', text: "How do I grow Turmeric?" },
];

// --- Components ---

const BottomNav = ({ activeTab, setActiveTab }) => {
  const navItems = [
    { id: 'home', icon: Home, label: 'Home' },
    { id: 'search', icon: Search, label: 'Search' },
    { id: 'chat', icon: MessageSquare, label: 'Chat' },
    { id: 'profile', icon: User, label: 'Profile' },
  ];

  return (
    <div className="fixed bottom-0 w-full bg-[#F5F7F2] border-t border-[#DDE2D5] px-6 py-3 flex justify-between items-center z-50 max-w-md mx-auto">
      {navItems.map((item) => (
        <button
          key={item.id}
          onClick={() => setActiveTab(item.id)}
          className={`flex flex-col items-center justify-center p-2 rounded-xl transition-all ${
            activeTab === item.id ? 'text-[#4A6741]' : 'text-[#8C9E88]'
          }`}
        >
          <item.icon size={24} strokeWidth={activeTab === item.id ? 2.5 : 2} />
        </button>
      ))}
    </div>
  );
};

// --- Screens ---

const HomeScreen = ({ onNavigate }) => {
  return (
    <div className="pb-24 min-h-screen bg-[#F5F7F2] animate-in fade-in">
      {/* Header */}
      <header className="p-6 pt-12 flex justify-between items-center bg-white shadow-sm">
        <div className="flex items-center gap-2">
           <div className="w-8 h-8 bg-[#4A6741] rounded-lg flex items-center justify-center text-white font-serif font-bold text-lg">H</div>
           <h1 className="text-xl font-bold text-[#2C3E28] font-serif">Heritage Garden Guide</h1>
        </div>
        <Menu className="text-[#4A6741]" />
      </header>

      {/* Hero Section */}
      <div className="px-6 py-6">
        <div className="rounded-3xl overflow-hidden shadow-md h-56 relative mb-6">
          <img 
            src="https://images.unsplash.com/photo-1585320806297-9794b3e4eeae?auto=format&fit=crop&q=80&w=800" 
            alt="Heritage House" 
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/70 via-transparent to-transparent flex items-end p-6">
            <p className="text-white font-medium text-sm tracking-wide">Traditional Malay House & Garden</p>
          </div>
        </div>

        <div className="mb-8 text-center">
          <h2 className="text-2xl font-serif font-bold text-[#2C3E28] mb-3">Welcome to the Heritage Garden!</h2>
          <p className="text-[#5C6F58] leading-relaxed text-sm max-w-xs mx-auto">
            Explore the culinary, medicinal and traditional plants that define our heritage. Start your journey below.
          </p>
        </div>

        {/* Action Buttons */}
        <div className="space-y-4 mb-8">
           <div className="flex items-center gap-2 mb-3">
             <div className="h-px bg-[#DDE2D5] flex-1"></div>
             <h3 className="font-bold text-[#2C3E28] text-sm uppercase tracking-widest">Start Exploring</h3>
             <div className="h-px bg-[#DDE2D5] flex-1"></div>
           </div>

           <div className="grid grid-cols-2 gap-4">
              <button 
                onClick={() => onNavigate('search')}
                className="bg-white p-5 rounded-2xl shadow-sm border border-[#E0E6DC] flex flex-col items-center gap-3 hover:bg-[#F0F5EE] transition-all hover:scale-[1.02] active:scale-95"
              >
                <div className="w-14 h-14 bg-[#E8F0E5] rounded-full flex items-center justify-center text-[#4A6741]">
                  <Search size={26} />
                </div>
                <span className="font-semibold text-[#2C3E28]">Search Plants</span>
              </button>

              <button 
                onClick={() => onNavigate('chat')}
                className="bg-white p-5 rounded-2xl shadow-sm border border-[#E0E6DC] flex flex-col items-center gap-3 hover:bg-[#F0F5EE] transition-all hover:scale-[1.02] active:scale-95"
              >
                <div className="w-14 h-14 bg-[#E8F0E5] rounded-full flex items-center justify-center text-[#4A6741]">
                  <MessageSquare size={26} />
                </div>
