const ChatScreen = () => {
  const [messages, setMessages] = useState(CHAT_MESSAGES);
  const [input, setInput] = useState("");
  const endRef = useRef(null);

  const handleSend = () => {
    if (!input.trim()) return;
    setMessages([...messages, { id: Date.now(), sender: 'user', text: input }]);
    setInput("");
    setTimeout(() => {
      setMessages(prev => [...prev, { id: Date.now() + 1, sender: 'bot', text: "I'm still learning about the garden! Ask me about specific plants like Brazilian Spinach." }]);
    }, 1000);
  };

  useEffect(() => {
    endRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="flex flex-col h-screen bg-[#F5F7F2] animate-in fade-in">
       {/* Header */}
       <div className="pt-12 pb-4 px-6 bg-white border-b border-[#E0E6DC] shadow-sm flex items-center gap-3">
         <ChevronLeft className="text-[#4A6741]" />
         <h2 className="text-xl font-serif font-bold text-[#2C3E28]">AI Garden Assistant</h2>
       </div>

       {/* Chat Area */}
       <div className="flex-1 overflow-y-auto p-6 space-y-6 pb-32">
          {messages.map((msg) => (
            <div key={msg.id} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div 
                className={`max-w-[85%] p-4 rounded-2xl text-sm leading-relaxed shadow-sm ${
                  msg.sender === 'user' 
                    ? 'bg-[#4A6741] text-white rounded-tr-none' 
                    : 'bg-white text-[#2C3E28] border border-[#E0E6DC] rounded-tl-none'
                }`}
              >
                {msg.text.split('\n').map((line, i) => (
                  <React.Fragment key={i}>
                    {line}
                    {i < msg.text.split('\n').length - 1 && <br />}
                  </React.Fragment>
                ))}
              </div>
            </div>
          ))}
          <div ref={endRef} />
       </div>

       {/* Input Area */}
       <div className="fixed bottom-[75px] left-0 w-full px-4 pb-4 bg-gradient-to-t from-[#F5F7F2] via-[#F5F7F2] to-transparent pt-6 max-w-md mx-auto">
         <div className="max-w-md mx-auto relative shadow-lg rounded-full">
           <input 
             value={input}
             onChange={(e) => setInput(e.target.value)}
             onKeyDown={(e) => e.key === 'Enter' && handleSend()}
             placeholder="Ask anything about the garden..." 
             className="w-full bg-white border border-[#DDE2D5] rounded-full py-4 pl-6 pr-14 text-sm focus:outline-none focus:ring-2 focus:ring-[#8AA883] text-[#2C3E28]"
           />
           <button 
             onClick={handleSend}
             className="absolute right-2 top-2 p-2 bg-[#4A6741] text-white rounded-full hover:bg-[#3D5535] transition-colors shadow-md"
           >
             <Send size={18} />
           </button>
         </div>
       </div>
    </div>
  );
};

// --- Main App Component ---

export default function App() {
  const [activeTab, setActiveTab] = useState('home');
  const [selectedPlant, setSelectedPlant] = useState(null);

  const handleNavigate = (tab) => {
    setActiveTab(tab);
    setSelectedPlant(null); // Reset detail view when switching tabs
  };

  const handlePlantClick = (plant) => {
    setSelectedPlant(plant);
  };

  const renderContent = () => {
    if (activeTab === 'home') return <HomeScreen onNavigate={handleNavigate} />;
    if (activeTab === 'search') {
      return selectedPlant 
        ? <PlantDetailScreen plant={selectedPlant} onBack={() => setSelectedPlant(null)} />
        : <SearchScreen onPlantClick={handlePlantClick} />;
    }
    if (activeTab === 'chat') return <ChatScreen />;
    return <HomeScreen onNavigate={handleNavigate} />;
  };

  return (
    <div className="font-sans antialiased bg-gray-100 min-h-screen max-w-md mx-auto relative shadow-2xl overflow-hidden border-x border-gray-200">
      <main className="h-full bg-[#F5F7F2]">
        {renderContent()}
      </main>
      <BottomNav activeTab={activeTab} setActiveTab={handleNavigate} />
    </div>
  );
}
